import React, { Component } from 'react'

export default class SignUp extends Component {
    //get value
    constructor(props){
        super(props)
        this.state={
            fname:"",
            lname:"",
            suffix:"",
            email:"",
            password:"",
            selectedOption: "female"
        };
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    handleSubmit(e){
      e.preventDefault();
        const{fname, lname, suffix, email, password, gender} = this.state;
        console.log(fname, lname, suffix, email, password, gender);
        fetch("http://localhost:5000/register", {
          method: "POST",
          crossDomain: true,
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
            "Access-Control-Allow-Origin":"*",
          },
          body: JSON.stringify({
            fname, lname, suffix, email, password, gender
          }),
        }).then((res)=>res.json())
        .then((data)=> {
          console.log(data, "userRegister");
        });
    }

    handleOptionChange = changeEvent => {
      this.setState({
        selectedOption: changeEvent.target.value
      });
    };

    handleAll = () => {
      this.handleSubmit();
      this.handleOptionChange();
    }


  render() {
    return (
      <form onSubmit={this.handleAll}>
        <h3>Sign Up</h3>

        <div className="mb-3">
          <label>First name</label>
          <input
            type="text"
            className="form-control"
            placeholder="First name"
            onChange={(e) => this.setState({ fname: e.target.value})}
            required
          />
        </div>

        <div className="mb-3">
          <label>Last name</label>
          <input type="text" className="form-control" placeholder="Last name"
          onChange={(e) => this.setState({ lname: e.target.value})} 
          required
          />
        </div>

        <div className="mb-3 suffix">
          <label>Suffix</label>
          <input type="text" className="form-control suffix" placeholder="(e.g. Jr. , Sr., II)"
          onChange={(e) => this.setState({ suffix: e.target.value})} />
        </div>

        <div className="mb-3">
          <label>Email address</label>
          <input
            type="email"
            className="form-control"
            placeholder="Enter email"
            onChange={(e) => this.setState({ email: e.target.value})}
            required
          />
          
        </div>

        <div className="mb-3">
          <label>Password</label>
          <input
            type="password"
            className="form-control"
            placeholder="Enter password"
            onChange={(e) => this.setState({ password: e.target.value})}
            required
          />
        </div>

        <div className="mb-3">
        <label>Gender</label>
        <br/>
        <div className='form-check'>
        <input
              type="radio"
              className="form-check-input"
              value="female"
              name = "gender"
              checked = {this.state.selectedOption === "female"}
              required
            /> Female   
        </div>

        <div className='form-check'>
        <input
              type="radio"
              className="form-check-input"
              name = "gender"
              checked = {this.state.selectedOption === "male"}
              value="male"
              required
            /> Male
        </div>
          </div>


        <div className="d-grid">
          <button type="submit" className="btn btn-primary">
            Sign Up
          </button>
        </div>



        <p className="forgot-password text-right">
          Already registered? <a href="/sign-in">Sign In</a>
        </p>
      </form>

    )
  }
}
